export const apiClient = {
    getLists: async () => {
        return axios.get('http://localhost:3000/lists').then(res=>res.data)
    },
    addList: async (listName: string) => {
        const addedList = axios.post('http://localhost:3000/lists', {id: listName, description: listName}).then(res=>res.data)

        console.debug('-- addList', listName, addedList);
        return Promise.resolve(addedList)
    },
    getTodos: async (listName: string): Promise<string[]> => {
            return axios.get(`http://localhost:3000/lists`).then((response)=>
            response.data.find((list:ITodoList) => list.id === listName).then((list:ITodoList) => list.items?.map((item:IItem) => item.nom) )
        )        
